mapper.select:
select

	distinct


from et_garage_core

	


	order by ${orderByClause}


	limit #{page.begin} , #{page.length}

select count(*) FROM et_garage_core g LEFT JOIN et_org_info o ON g.org_id=o.org_code WHERE g.category_code IN('001','003')
and g.garage_name like CONCAT('%',#{garageName},'%')
and g.garage_code like CONCAT('%',#{garageCode},'%')
and g.repair_tel like CONCAT('%',#{repairTel},'%')
and o.org_id = #{orgId,jdbcType=VARCHAR}
and g.company_code = #{companyCode,jdbcType=VARCHAR}

select g.*,o.org_desc
FROM et_garage_core g LEFT JOIN et_org_info o ON g.org_id=o.org_code
WHERE g.category_code IN('001','003')

	and g.garage_name like CONCAT('%',#{garageName},'%')


	and g.garage_code like CONCAT('%',#{garageCode},'%')


	and g.repair_tel like CONCAT('%',#{repairTel},'%')


	and o.org_id = #{orgId,jdbcType=VARCHAR}


	and g.company_code = #{companyCode,jdbcType=VARCHAR}


	limit #{skip}, #{limit}
